import React from 'react';

// Card Component: Takes dynamic title, description, image, and background color as props
const Card = ({ title, description, imageUrl, bgColor, borderColor }) => (
  <div
    className={`p-6 
                rounded-lg 
                shadow-xl 
                ${bgColor} 
                ${borderColor}
                bg-cover 
                bg-center 
                transition-transform 
                transform 
                hover:scale-105 
                hover:bg-opacity-80 
                hover:bg-blue-700`}
    style={{ backgroundImage: `url(${imageUrl})` }}
  >
    {/* Stripes using pseudo-elements */}
    <div
      className="absolute inset-0 z-0 border-8 border-transparent rounded-lg"
      style={{
        background: 'linear-gradient(45deg, orange 25%, white 25%, white 50%, green 50%, green 75%, orange 75%)',
        backgroundSize: '8px 8px', // Adjust stripe thickness
        opacity: 0.8, // Set opacity for subtle stripes
      }}
    />

    <div className="relative z-10 p-4 bg-opacity-60 bg-black rounded-lg">
      <h2 className="text-3xl font-semibold text-white">{title}</h2>
      <p className="text-lg text-white">{description}</p>
    </div>
  </div>
);

export default Card;
