// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";  // Import useNavigate
import './index.css'; // Ensure this line is present

// Card Component: Takes dynamic title, description, image, and background color as props
const Card = ({ title, description, imageUrl, bgColor }) => (
  <div
    className={`p-6 rounded-lg shadow-xl ${bgColor} bg-cover bg-center`}
    style={{ backgroundImage: `url(${imageUrl})` }}
  >
    <div className="p-4 bg-opacity-60 bg-black rounded-lg">
      <h2 className="text-3xl font-semibold text-white">{title}</h2>
      <p className="text-lg text-white">{description}</p>
    </div>
  </div>
);

// Button Component: Reusable button component
const Button = ({ children, className }) => (
  <button className={`px-8 py-3 text-lg rounded-lg ${className} transition duration-300 transform hover:scale-105`}>
    {children}
  </button>
);

// Landing Page Component with dynamic content
const LandingPage = () => {
  const [cards] = useState([
    {
      title: "History",
      description: "Explore the rich historical legacy of India, from ancient civilizations to modern times.",
      imageUrl: "https://via.placeholder.com/500x300/ff7f7f/333333",
      bgColor: "bg-gradient-to-r from-orange-500 to-red-600",
    },
    {
      title: "Festivals",
      description: "Dive into the vibrant and diverse festivals celebrated across India.",
      imageUrl: "https://via.placeholder.com/500x300/ffd700/333333",
      bgColor: "bg-gradient-to-r from-yellow-400 to-orange-500",
    },
    {
      title: "Cuisine",
      description: "Taste the diverse and spicy flavors of Indian cuisine from every region.",
      imageUrl: "https://via.placeholder.com/500x300/32cd32/333333",
      bgColor: "bg-gradient-to-r from-green-400 to-teal-500",
    },
  ]);

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="bg-blue-500 p-6">
        <h1 className="text-white text-3xl">Tailwind Test: Background should be blue</h1>
      </div>

      <div className="text-center mb-12">
        <h1 className="text-5xl font-extrabold text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-blue-500">
          Discover Indian Culture & Heritage
        </h1>
        <p className="text-xl mt-4 text-gray-600">
          Explore India's history, festivals, cuisine, and more. Immerse yourself in the diversity and vibrancy.
        </p>
      </div>

      <div className="grid md:grid-cols-3 gap-6 mb-12">
        {cards.map((card, index) => (
          <Card
            key={index}
            title={card.title}
            description={card.description}
            imageUrl={card.imageUrl}
            bgColor={card.bgColor}
          />
        ))}
      </div>

      <div className="text-center mt-6">
        <Link to="/feedback">
          <Button className="bg-yellow-500 text-black hover:bg-yellow-600">
            Give Feedback
          </Button>
        </Link>
      </div>
    </div>
  );
};

// Feedback Page Component
const FeedbackPage = () => {
  const navigate = useNavigate();  // Initialize the navigate function

  // Function to handle the back navigation
  const handleBackClick = () => {
    // Check if there is a previous page in history
    if (window.history.length > 1) {
      // If history exists, navigate back
      navigate(-1);
    } else {
      // If no history exists, navigate to the home page
      navigate('/');
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-4">Feedback</h1>
      <textarea
        className="w-3/4 p-3 border border-gray-400 rounded-lg"
        rows="5"
        placeholder="Share your thoughts..."
      ></textarea>

      {/* Submit Button */}
      <Button className="mt-4 bg-green-500 hover:bg-green-600">Submit</Button>

      {/* Back Button */}
      <Button
        className="mt-4 bg-yellow-500 hover:bg-yellow-600"
        onClick={handleBackClick}  // Use handleBackClick to navigate back or to home
      >
        Back
      </Button>
    </div>
  );
};

// Main App with Routes
const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/feedback" element={<FeedbackPage />} />
      </Routes>
    </Router>
  );
};

export default App;
